import React, { useRef } from 'react';
import emailjs from 'emailjs-com';

const ForgetPassword = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault(); // prevents the page from reloading when you hit “Send”

    const token = generateRandomToken(); // Generate a random token

    // Get the user's email address from the form
    const userEmail = form.current.user_email.value;

    // Concatenate the token with the email link
    const emailLink = `https://example.com/reset-password?token=${token}`;

    // Send the email with the concatenated link
    emailjs.sendForm('service_zgcb8cy', 'template_dx8gftp', form.current, '04RwhtqN7IMklYKNm')
      .then((result) => {
        console.log('Reset link is successfully sent to the user!');
        console.log('Email Link:', emailLink);
      })
      .catch((error) => {
        console.log('An error occurred while sending the email:', error);
      });
  };

  const generateRandomToken = () => {
    // Generate a random token implementation
    // Here is an example of generating a random 8-character token using Math.random()
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let token = '';
    for (let i = 0; i < 8; i++) {
      token += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return token;
  };

  return (
    <form ref={form} onSubmit={sendEmail}>
      <label>Email</label>
      <input type="email" name="user_email" />
      <input type="hidden" name="message" />
      <input type="submit" value="Send" />
    </form>
  );
};

export default ForgetPassword
