import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';
import { jsxDEV } from 'react/jsx-dev-runtime';


const ForgetPassword = () => {
 const form = useRef();

 const sendEmail = (e) => {
   e.preventDefault(); // prevents the page from reloading when you hit “Send”

   emailjs.sendForm("service_zgcb8cy","template_dx8gftp", form.current, "04RwhtqN7IMklYKNm")
     .then((result) => {
         console.log("Reset link is successfully sent to user!!! ")
     }, (error) => {
        console.log("This user does not found!")
     });
 };

 return (
   <form ref={form} onSubmit={sendEmail}>
     {/* <label>Name</label>
     <input type="text" name="user_name" /> */}
     <label>Email</label>
     <input type="email" name="user_email" />
     <label>Message</label>
     <textarea type="hidden" name="message" />
     <input type="submit" value="Send" />
   </form>
 );
};

export default ForgetPassword;
